INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('9', 'Nubuck Palm for Abrasion Resistance. Foam Padding Provides Slight Impact Protection. Reinforced Thumb Saddle and Fingertips for Added Durability. Touch Screen Index Fingertip. Lightweight and Breathable. Machine Washable', 'https://www.ruralking.com/media/catalog/product/e/c/ec066b654679a3249c46bf4e270b097720d4f513_12930003.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'High Performance Work Gloves, 3 Pairs', '7.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('10', 'Motomco’s complete mole control program includes mole killer, a revolutionary mole bait, and Motomco’s new mole trap. Scientifically proven to kill moles, mole killer resembles their natural food - earthworms and grubs - in size, shape and feel. This breakthrough product was developed and tested on live, captured moles and contains special enhancers to attract moles. It kills in just a single feeding. Moles usually die in their tunnel, often within 12-24 hours of ingesting a single worm. Mole killer is EPA-approved for use by homeowners on lawns and gardens.', 'https://www.ruralking.com/media/catalog/product/f/f/ffb681c025ec482ac677f0facb3cf7beb143bc58_003890592_1_1.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Motomco Mole Killer Earthworm Formation, 8 Pack ', '21.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('11', 'This forged bow rake is high quality and designed for light-duty jobs around the lawn and garden. The tine side of the rake head is used to break up and cultivate the soil, while the back side of the rake is great for leveling soil and other materials.', 'https://www.ruralking.com/media/catalog/product/b/3/b3165ff6edbcbc7aea3aecb2fd9124675b2f9c2f_5071513_5071513_image_5071513.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Truper 14 Tine Welded Bow Rake', '8.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('12', 'The versatile two-sided bow rake is designed to loosen and level soil. The steel tines work hard to break up compacted soil or mulch, while the back of the rake head is good for leveling and smoothing. A great value for all your landscaping and gardening needs.', 'https://www.ruralking.com/media/catalog/product/a/5/a5eefc89b0621d32bb327f2d4229384288d4c827_10980013_10980013_image_10980013.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Truper 53" Fiberglass Bow Rake', '17.99');
