INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('36', 'This soil scoop is lightweight for easy handling and durable for lasting value. A generous scoop helps you get more done faster, and a single-piece, rustproof FiberComp design will never loosen or bend. In addition, a handle hang hole makes storage convenient.', 'https://www.ruralking.com/media/catalog/product/c/a/cae3a9f99a49f0624133c80c719c21441692f381_5420368_5420368_image_5420368.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Soil Scoop', '1.99');
