INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('40', 'Durable stainless steel blades. Self-sharpening serration blade.', 'https://www.ruralking.com/media/catalog/product/1/c/1c62769fffa99de49480c9502e5d6fdbe7355e5f_005070789.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Grass Shears 12.5 inch', '9.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('41', 'Strap yourself in & make spraying a breeze with the Master Manufacturing 4 gallon backpack sprayer.', 'https://www.ruralking.com/media/catalog/product/8/5/85d7dd7b5ab71d9d59756cd2b4b79eae97199969_7813636.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', '4 Gallon Backpack Sprayer', '69.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('42', 'This Maple Ridge steel rake is used for clearing leaves and debris from lawns. The flexible steel tines of the garden rake will pull out thatch for a healthier lawn. The one-piece steel head of the leaf rake is attached to the handle for a secure connection while raking. The leaf rake also features a sturdy wooden handle.', 'https://www.ruralking.com/media/catalog/product/5/8/5847841c868bff3846c8ac9edd979651e0d68275_33580002.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Maple Ridge 30" Metal Rake with Wooden Handl', '12.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('43', 'Extra-large removable lid allows easy access for filling and dumping. With your compost ingredients enclosed inside the composter tub, you will not be subjected to an unsightly, smelly, fly attracting, open-air compost heap.', 'https://www.ruralking.com/media/catalog/product/a/3/a38c42c3f24ca6c7c086bc865a14f9e2b6640b26_lifetime_65_gallon_compost_tumbler.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', '65 Gallon Compost Tumbler', '169.97');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('44', 'Composting lets you turn yard waste and kitchen scraps into homemade garden compost. Composting is Nature''s perfect recycling plan. The Gronomics Solar Assist Composter allows the waste to breakdown efficiently by letting the sunlight pass through the transparent roof. The Composter is made of 100% Western Red Cedar and features rust resistant wire for side panels. The corrugated poly roof is hinged which allows easy access from either side and is virtually unbreakable.  It is light bronze in color for UV protection and offers solar heating insulation.  The composter holds 9 cubic feet of compostable material and features tool free assembly which simply slides together in minutes.', 'https://www.ruralking.com/media/catalog/product/f/6/f6939efef326cd225b0d45ca2f4869df0f8a6898_gronomics_small_solar_assist_composter_sac_24_45_3.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Small Solar Assist Composter', '349.97');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('45', 'Multi-purpose glove great for everyday jobs. Latex double-dipped formed glove. Breathable and comfortable. Provides grip in wet or dry conditions. Nylon/spandex used on the back of the hand for a better, tighter fit. Constructed of 7 gauge yarn for more comfort and faster drying.', 'https://www.ruralking.com/media/catalog/product/d/5/d5d2d7b0274a83243fa7bb05cab5d417de6ee605_49181974.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Grip Work Gloves - X-Large', '8.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('46', 'Heavy-Duty Steel, Double Bolted.', 'https://www.ruralking.com/media/catalog/product/7/b/7b67a7d8fca282ca8155194fc9c8dfd559e7e612_63290077_63290077_image_87610.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Industrial Brush Cutter', '35.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('47', 'Support tomatoes, peppers and other plants with these 16 x 42". Heavy Duty Inverted Plant Supports. Featuring 3/16" American galvanized wire, this cage will look great and resist rust for years of use.', 'https://www.ruralking.com/media/catalog/product/b/9/b9369040255c0157a830c825be751e1b6d81fe6d_53670144_53670144_image_53670144.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Heavy Duty Galvanized Inverted Support', '4.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('48', 'Black Kow 50 lb. Composted Cow Manure is an organic soil amendment for vegetable gardens, flower beds, lawns and landscapes. It is a natural organic product that is aerobically composted and adds millions of beneficial microbes to enhance the soil structure and promote healthy plant growth. Black Kow has a 0.5-0.5-0.5 fertilizer analysis and provides nutrients for your flowers and vegetables', 'https://www.ruralking.com/media/catalog/product/b/e/beb0a5a3a1290b3df8b310b3f67fce37c392a30f_197330814.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Black Kow Mushroom Compost', '5.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('49', 'Instantly tells you how acid or alkaline your soil is. Check which plants suit your garden. Enables you to alter soil conditions.', 'https://www.ruralking.com/media/catalog/product/0/b/0bee3e2ad4df0663ba85ac7e6c9414304aa0c2d3_022880033.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'Rapitest PH Meter Battery Powered', '9.99');
INSERT INTO supplies (supply_id, description, image_url, supply_name, supply_cost) VALUES ('50', 'The modular raised garden bed is designed to easily expand with your garden needs and to create unlimited multi-level designs. Modular raised garden beds are ideal for small plots of vegetables and/or flowers. With these beds you eliminate tilling, soil amending, and minimize weeding. They are quick and easy to assemble plant and maintain. Easily adapt square foot gardening techniques to these beds. The rough sawn rustic appearance looks great in any setting. 100% Western red cedar.  Handcrafted in the USA!!', 'https://www.ruralking.com/media/catalog/product/f/7/f7ef92d365092778d2add47edd7faa905e67526f_gronomics_raised_one_level_modular_raised_garden_bed_mrgb_1l_48_48_4.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=560&width=700&canvas=700:560', 'One Level Modular Raised Garden Bed', '64.97');
